import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import { Button, Card, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Todo({ todo, index, markTodo, removeTodo}) {
    return (
      <div className="todo">
        <span style={{ textDecoration: todo.isDone ? "line-through" : "" }}>{todo.text}</span>
        <div>
          <Button variant="outline-success" onClick={() => markTodo(index)}>✓</Button>{' '}
          <Button variant="outline-danger" onClick={() => removeTodo(index)}>✕</Button>  
        </div>
      </div>
    );
  }

  function FormTodo({ addTodo }) {
    const [value, setValue] = React.useState("");

    const handleSubmit = e => {
      e.preventDefault();
      if (!value) return;
      addTodo(value);
      setValue("");
    };

    return (
      <Form onSubmit={handleSubmit}> 
      <Form.Group>
        <Form.Label><b>Add Todo</b></Form.Label>
        <Form.Control type="text" className="input" value={value} onChange={e => setValue(e.target.value)} placeholder="Add new todo" />
      </Form.Group>
      <Button variant="primary mb-3" type="submit">
        Submit
      </Button>
    </Form>
    );
  }

export const Main =styled.div`
  width: 450px;
  height: 600px;
  border: 2px solid black;
  border-radius: 0 0 25px 25px;
  background-color: antiquewhite;
  text-align: center;
  position: relative;
  z-index: 1;
`;
export const Carrier = styled.div`
  display: flex;
  flex-direction: row;
  position: relative;
  z-index: 2;
  background-color: rgba(255, 255, 255, 0);
`;

export const AvatarIcon = styled.button`
  width: 50px;
  height: 50px;
  border-radius: 25px;
  background-color: black;
`;

export const ModalBackdrop = styled.div`
  position: fixed;
  z-index: 999;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background-color: rgba(0,0,0,0.4);
  display: grid;
  place-items: center;
`;

export const ModalContainer = styled.div`
  height: 15rem;
  text-align: end;
  margin: 120px auto;
  border: 3px solid red;
  z-index: 2;
  bottom: 10rem;
`;

export const ModalBtn = styled.button`
  background-color: rgba(255,255,255,0);
  border: none;
  padding: 5rem;
  border-radius: 30px;
  cursor: pointer;
`;

export const ModalView = styled.div.attrs(props => ({
  // attrs 메소드를 이용해서 아래와 같이 div 엘리먼트에 속성을 추가할 수 있습니다.
  role: 'dialog'
}))`
    border-radius: 10px;
    border: 2px solid black;
    background-color: rgba(255,255,255,0);
    width: 250px;
    height: 150px;

    > span.close-btn {
      margin-top: 5px;
      cursor: pointer;
    }

    > div.desc {
      margin-top: 25px;
      color: rgba(255,255,255,0);
    }
`;

function IconCarrier(){
    const [todos, setTodos] = React.useState([
        {
          text: "This is a sample todo",
          isDone: false
        }
      ]);

      const addTodo = text => {
        const newTodos = [...todos, { text }];
        setTodos(newTodos);
      };

      const markTodo = index => {
        const newTodos = [...todos];
        newTodos[index].isDone = true;
        setTodos(newTodos);
      };

      const removeTodo = index => {
        const newTodos = [...todos];
        newTodos.splice(index, 1);
        setTodos(newTodos);
      };

  const [isOpen, setIsOpen] = useState(false);
  const openModalHandler = () => {
    setIsOpen(!isOpen);
  };
  return (
    <><Main>
    <div>
    <div className="app">
      <div className="container">
        <h1 className="text-center mb-4">Todo List</h1>
        <FormTodo addTodo={addTodo} />
        <div>
          {todos.map((todo, index) => (
            <Card>
              <Card.Body>
                <Todo
                key={index}
                index={index}
                todo={todo}
                markTodo={markTodo}
                removeTodo={removeTodo}
                />
              </Card.Body>
            </Card>
          ))}
        </div>
      </div>
    </div>
    </div>
      <ModalContainer>
        <ModalBtn onClick={openModalHandler}>
          {isOpen === false ? <img className="photosize" src="https://cdn-icons-png.flaticon.com/512/1828/1828817.png"/> : <img className="photosize" src="https://cdn-icons-png.flaticon.com/512/1828/1828919.png"/>}
        </ModalBtn>
        {isOpen === true ? <ModalBackdrop onClick={openModalHandler}>
          <ModalView onClick={(e) => e.stopPropagation()}>
            <span onClick={openModalHandler} className='close-btn'>&times;</span>
            <div className='desc'>
                <Carrier>
                    <AvatarIcon/>
                    <AvatarIcon/>
                    <AvatarIcon/>
                    <AvatarIcon/>
                    <AvatarIcon/>
                </Carrier>
            </div>
          </ModalView>
        </ModalBackdrop> : null}
      </ModalContainer>
      </Main>
    </>
  );
};  
export default IconCarrier;
